import React, { useState, useEffect } from 'react';
import axios from 'axios';

const KPISelector = () => {
  const [kpis, setKpis] = useState([]);
  const [selectedKpis, setSelectedKpis] = useState([]);
  const [recommendedTools, setRecommendedTools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchKPIs = async () => {
      try {
        console.log('Fetching KPIs from backend...');
        const response = await axios.get('http://localhost:3001/api/getKPIs');
        console.log('Raw response from backend:', response);
        console.log('KPIs received:', JSON.stringify(response.data, null, 2));
        response.data.forEach((kpi, index) => {
          console.log(`KPI ${index}:`, kpi);
          console.log(`KPI ${index} id:`, kpi.id);
          console.log(`KPI ${index} name:`, kpi.name);
        });
        setKpis(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching KPIs:', err);
        setError('Failed to load KPIs. Please try again later.');
        setLoading(false);
      }
    };

    fetchKPIs();
  }, []);

  const handleKpiSelect = (kpi) => {
    setSelectedKpis(prev => prev.some(k => k.id === kpi.id) ? prev : [...prev, kpi]);
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/recommend', { kpis: selectedKpis });
      console.log('Recommended tools response:', response.data);
      response.data.forEach((tool, index) => {
        console.log(`Tool ${index}:`, tool);
        console.log(`Tool ${index} id:`, tool.id);
        console.log(`Tool ${index} name:`, tool.name);
      });
      setRecommendedTools(response.data);
    } catch (err) {
      console.error('Error fetching recommendations:', err);
      setError('Failed to get recommendations. Please try again later.');
    }
  };

  if (loading) return <div>Loading KPIs...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Select KPIs:</h2>
      {console.log('KPIs:', kpis)}
      <ul>
        {kpis.map((kpi, index) => (
          <li key={`kpi-${kpi.id || index}`}>
            <button onClick={() => handleKpiSelect(kpi)}>{kpi.name}</button>
          </li>
        ))}
      </ul>
      <h2>Selected KPIs:</h2>
      {console.log('Selected KPIs:', selectedKpis)}
      <ul>
        {selectedKpis.map((kpi, index) => (
          <li key={`selected-kpi-${kpi.id || index}`}>{kpi.name}</li>
        ))}
      </ul>
      <button onClick={handleSubmit}>Get Recommended Tools</button>
      <h2>Recommended Tools:</h2>
      {console.log('Recommended Tools:', recommendedTools)}
      <ul>
        {recommendedTools.map((tool, index) => (
          <li key={`tool-${tool.id || tool.name || index}`}>{tool.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default KPISelector;
