import React, { useState, useEffect } from 'react';
import axios from 'axios';

const KPISelector = () => {
  const [kpis, setKpis] = useState([]);
  const [selectedKpis, setSelectedKpis] = useState([]);
  const [recommendedTools, setRecommendedTools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchKPIs = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/getKPIs');
        setKpis(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching KPIs:', err);
        setError('Failed to load KPIs. Please try again later.');
        setLoading(false);
      }
    };

    fetchKPIs();
  }, []);

  const handleKpiSelect = (kpi) => {
    setSelectedKpis(prev => {
      const isAlreadySelected = prev.some(k => k.name === kpi.name);
      if (isAlreadySelected) {
        return prev.filter(k => k.name !== kpi.name);
      } else {
        return [...prev, kpi];
      }
    });
  };

  const handleSubmit = async () => {
    if (selectedKpis.length === 0) {
      setError('Please select at least one KPI');
      return;
    }
    try {
      const response = await axios.post('http://localhost:3001/api/recommend', { kpis: selectedKpis });
      setRecommendedTools(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching recommendations:', err);
      setError('Failed to get recommendations. Please try again later.');
    }
  };

  if (loading) return <div>Loading KPIs...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Select KPIs:</h2>
      <ul>
        {kpis.map((kpi) => (
          <li key={`kpi-${kpi.name}`}>
            <label>
              <input
                type="checkbox"
                checked={selectedKpis.some(k => k.name === kpi.name)}
                onChange={() => handleKpiSelect(kpi)}
              />
              {kpi.name}
            </label>
          </li>
        ))}
      </ul>
      <h2>Selected KPIs:</h2>
      <ul>
        {selectedKpis.map((kpi) => (
          <li key={`selected-kpi-${kpi.name}`}>{kpi.name}</li>
        ))}
      </ul>
      <button onClick={handleSubmit}>Get Recommended Tools</button>
      <h2>Recommended Tools:</h2>
      {recommendedTools.length > 0 ? (
        <ul>
          {recommendedTools.map((tool) => (
            <li key={`tool-${tool.name}`}>{tool.name}</li>
          ))}
        </ul>
      ) : (
        <p>No tools found that can calculate all selected KPIs.</p>
      )}
    </div>
  );
};

export default KPISelector;
